ValidationRule.errorConditionFormula:
IF(
    ISCHANGED(Status__c),
    AND(
        NOT(
            OR(
                ISPICKVAL(Status__c, 'Draft'),
                ISPICKVAL(Status__c, 'Submitted')

            )
        ),
        $User.ProfileId = '00e1t000000HkGa' || $User.ProfileId = '00e3X000001RysS'
    ),
    null
)
